mapper.select:
SELECT *
FROM quizzes q
WHERE id = #{qid};

SELECT
q.id
FROM quizzes q
WHERE memberId = #{memberId} AND id=(
SELECT max(id) FROM quizzes
);

mapper.insert:
/* 회원가입 */
INSERT INTO quizzes
(	  memberId,
title,
subjectId,
quizCreateDateTime,
ShuffleQuestions,
ShuffleOptions,
allowedAttempts
)VALUES (
#{quizzesMemberId}
, #{quizzesTitle}
, 193
, NOW()
, #{quizzesShuffleQuestions}
, #{quizzesShuffleOptions}
, #{quizzesAllowedAttempts}
);

/* 회원가입 */
INSERT INTO questions
(	  quizId,
question,
questionType
)VALUES (
#{questionsQuizId}
, #{questionsQuestion}
, #{questionQuestionType}
);

/* 회원가입 */
INSERT INTO questionOptions
( questionId,
optionContent,
isAnswer
)VALUES (
#{questionOptionQuestionId}
, #{questionOptionOptionContent}
, #{questionOptionIsAnswer}
);

INSERT INTO quizHistory
(memberId,
quizId,
grade,
startDateTime,
endDateTime
)VALUES (
#{quizHistoryMemberId},
#{quizHistoryQuizId},
#{quizHistoryGrade},
#{quizHistoryStartDateTime},
#{quizHistoryEndDateTime}
);

mapper.select:
SELECT
q.id
FROM quizHistory q
WHERE memberId = #{memberId} AND id=(
SELECT max(id) FROM quizHistory
);

mapper.insert:
INSERT INTO questionHistory
(	  quizHistoryId,
questionId,
userResponse,
isCorrected
)VALUES (
#{questionsHistoryQuizHistoryId},
#{questionsHistoryQuestionId},
#{questionsHistoryUserResponse},
#{questionsHistoryIsCorrected}
);

mapper.select:
SELECT q.id AS quizzesId, q.title AS quizzesTitle, q.averageScore AS averageScore, q.totalQuestions AS totalQuestions, q.quizCreateDateTime AS postedDate,
s.institutionLevel as educationalLevel, 2 as year, s.generalSubject as generalSubject, s.detailSubject as detailSubject,
m.userId as creatorId FROM quizzes as q RIGHT JOIN subjects as s ON q.subjectId = s.id LEFT JOIN members as m ON q.memberId = m.id WHERE q.id = #{qid}
